CREATE TABLE users(user_id INT GENERATED ALWAYS AS IDENTITY, name VARCHAR(64) NOT NULL, email_address VARCHAR(64) NOT NULL, phone VARCHAR(64) NOT NULL, password VARCHAR(64) NOT NULL, verify_password VARCHAR(64) NOT NULL);
CREATE TABLE detail_recipe(recipe_id INT GENERATED ALWAYS AS IDENTITY, name_food VARCHAR(64) NOT NULL, picture VARCHAR(64) NOT NULL, ingrediens TEXT NOT NULL,  video VARCHAR(64) NOT NULL , comment TEXT);